import React from 'react';
import DraggableCard from './DraggableCard';

const PlayerHand = ({ player, cards, isCurrent, onSelectCard, selectedCard }) => {
  return (
    // We always render both hands to avoid violating rules of hooks,
    // but we use CSS to hide the non-active player's hand.
    <div
      className={`player-hand ${isCurrent ? 'current-player' : ''}`}
      style={{ display: isCurrent ? 'block' : 'none' }}
    >
      <h3>{`Player ${player + 1} ${isCurrent ? '(Your Turn)' : ''}`}</h3>
      <div className="cards-container" style={{ display: 'flex', gap: '5px', flexWrap: 'wrap', justifyContent: 'center' }}>
        {cards.map((card, index) => {
          const isSelected = selectedCard && card.rank === selectedCard.rank && card.suit === selectedCard.suit;

          return (
            <DraggableCard
              key={`${card.rank}-${card.suit}`}
              card={card}
              player={player}
              isCurrent={isCurrent}
              isSelected={isSelected}
              onSelectCard={onSelectCard}
            />
          );
        })}
      </div>
    </div>
  );
};

export default PlayerHand;
