import React from 'react';

function TableCards({ cards, onDragStart, selectedTableCards, onSelectTableCard }) {
  return (
    <div className="table-cards">
      <h3>Table Cards</h3>
      <div className="cards-container" style={{ position: 'relative', width: 'auto', height: '120px', display: 'flex', gap: '10px' }}>
        {cards.length === 0 && <p>No cards on table</p>}
        {cards.map((card, index) => {
          if (card.type === 'build' || card.type === 'stack') {
            // Render only top card of stack/build
            const topCard = card.cards[0]; // Smallest card is on top

            return (
              <div
                key={index}
                className="card" // No selection for builds for now
                style={{
                  color: ['♥', '♦'].includes(topCard.suit) ? 'red' : 'black',
                  position: 'relative',
                }}
              >
                <div className="card-rank">{topCard.rank}</div>
                <div className="card-suit">{topCard.suit}</div>
                {card.buildValue && (
                  <div
                    style={{
                      position: 'absolute',
                      top: '-15px',
                      right: '0',
                      backgroundColor: 'yellow',
                      border: '1px solid black',
                      borderRadius: '4px',
                      padding: '2px 5px',
                      fontWeight: 'bold',
                      fontSize: '12px',
                      userSelect: 'none',
                    }}
                  >
                    {card.buildValue}
                  </div>
                )}
              </div>
            );
          } else {
            // Loose card
            const isSelected = selectedTableCards.some(
                (c) => c.rank === card.rank && c.suit === card.suit
            );
            return (
              <div
                key={index}
                className={`card ${isSelected ? 'selected' : ''}`}
                draggable
                onDragStart={(e) => {
                  e.dataTransfer.setData('text/plain', JSON.stringify(card));
                  onDragStart(card);
                }}
                onClick={() => onSelectTableCard(card)}
                style={{
                  cursor: 'pointer',
                  color: ['♥', '♦'].includes(card.suit) ? 'red' : 'black',
                }}
              >
                <div className="card-rank">{card.rank}</div>
                <div className="card-suit">{card.suit}</div>
              </div>
            );
          }
        })}
      </div>
    </div>
  );
}

export default TableCards;
