import React from 'react';
import Card from './Card';

const CapturedCards = ({ player, cards }) => {
  // Show the top card of the capture pile
  const topCard = cards.length > 0 ? cards[cards.length - 1] : null;

  return (
    <div className="captured-cards" style={{ textAlign: 'center' }}>
      <h4>{`Player ${player + 1} Captured (${cards.length})`}</h4>
      <div className="cards-container" style={{ height: '105px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        {topCard ? (
          <Card rank={topCard.rank} suit={topCard.suit} />
        ) : (
          <div className="card-placeholder" style={{ width: '65px', height: '95px', border: '1.5px dashed grey', borderRadius: '10px', display: 'flex', alignItems: 'center', justifyContent: 'center', color: 'grey' }}>Empty</div>
        )}
      </div>
    </div>
  );
};

export default CapturedCards;
