import React from 'react';
import { useDrop } from 'react-dnd';
import CardStack from './CardStack';

const ItemTypes = {
  CARD: 'card',
};

const TableCards = ({ cards, onDropCard, onDropOnCard }) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: ItemTypes.CARD,
    drop: (item) => {
      if (!item.player && item.player !== 0) {
        alert("Invalid player information for drop.");
        return;
      }
      // We cannot access currentPlayer here, so we rely on onDropCard to validate
      onDropCard(item.card, item.player);
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  }));

  // Group loose cards by their rank.
  const looseCardGroups = cards
    .filter(c => !c.type)
    .reduce((groups, card) => {
      (groups[card.rank] = groups[card.rank] || []).push(card);
      return groups;
    }, {});

  const builds = cards.filter(c => c.type === 'build');

  return (
    <div ref={drop} className={`table-cards ${isOver ? 'hovered' : ''}`}>
      <h3>Table Cards</h3>
      <div className="cards-container" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', minHeight: '120px', alignItems: 'flex-start', gap: '10px' }}>
        {/* Render each group of loose cards as a stack */}
        {Object.values(looseCardGroups).map((group, index) =>
          <CardStack
            key={`loose-stack-${index}`}
            stackId={`loose-stack-${index}`}
            cards={group}
            onDropStack={(draggedCard, position) => {
              if (draggedCard.player !== undefined) {
                onDropOnCard(draggedCard, group[0], { clientX: position.x, clientY: position.y });
              } else {
                alert("Invalid player information for drop on stack.");
              }
            }}
          />
        )}
        {/* Render each build as a stack inside its own container */}
        {builds.map((build, index) => (
          <div key={`build-container-${index}`} className="build" style={{ border: '2px dashed green', padding: '5px', borderRadius: '10px' }}>
            <CardStack
              stackId={`build-stack-${index}`}
              cards={build.cards}
              onDropStack={(draggedCard, position) => {
                if (draggedCard.player !== undefined) {
                  onDropOnCard(draggedCard, build, { clientX: position.x, clientY: position.y });
                } else {
                  alert("Invalid player information for drop on build.");
                }
              }}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default TableCards;
