import React from 'react';

const TableCards = ({ cards, selectedCards, onSelectCard }) => {
  const handleSelectCard = (card) => {
    const isSelected = selectedCards.find((c) => c === card);
    if (isSelected) {
      onSelectCard(selectedCards.filter((c) => c !== card));
    } else {
      onSelectCard([...selectedCards, card]);
    }
  };

  return (
    <div className="table-cards">
      <h3>Table Cards</h3>
      <div className="cards-container">
        {cards.map((card, index) => (
          <div
            key={index}
            className={`card ${selectedCards.includes(card) ? 'selected' : ''}`}
            onClick={() => handleSelectCard(card)}
          >
            {card.rank}{card.suit}
          </div>
        ))}
      </div>
    </div>
  );
};

export default TableCards;
