import React, { useState } from 'react';
import PlayerHand from './PlayerHand';
import TableCards from './TableCards';

import {
  initializeGame,
  handleDrop,
  handleBuild,
  handleCapture,
  isRoundOver,
  isGameOver,
  calculateScores,
  findValidCaptures,
  findValidBuilds,
} from './game-logic';

function GameBoard({ onRestart }) {
  const [gameState, setGameState] = useState(initializeGame());

  const handleDropCard = (card, player) => {
    if (player !== gameState.currentPlayer) {
      console.log("It's not your turn!");
      return;
    }

    let newGameState = { ...gameState };
    let actionTaken = false;

    // 1. Check for Capture
    const possibleCaptures = findValidCaptures(card, gameState.tableCards);
    if (possibleCaptures.length > 0) {
      // For simplicity, take the first valid capture. In a real game, the player might choose.
      newGameState = handleCapture(gameState, card, possibleCaptures[0]);
      actionTaken = true;
    }

    // 2. Check for Build (if no capture was made)
    if (!actionTaken) {
      const possibleBuilds = findValidBuilds(card, gameState.tableCards);
      if (possibleBuilds.length > 0) {
        // For simplicity, take the first valid build. In a real game, the player might choose.
        newGameState = handleBuild(gameState, card, possibleBuilds[0]);
        actionTaken = true;
      }
    }

    // 3. Default to Drop (if no capture or build was made)
    if (!actionTaken) {
      newGameState = handleDrop(gameState, card);
    }

    setGameState(newGameState);
  };

  return (
    <div className="game-board">
      <div className="status-section">
        <p>Round: {gameState.round}</p>
        <p>Current Player: Player {gameState.currentPlayer + 1}</p>
        <p>Scores: Player 1 ({gameState.scores[0]}) - Player 2 ({gameState.scores[1]})</p>
      </div>
      <div className="table-cards-section">
        <TableCards
          cards={gameState.tableCards}
          onDropCard={handleDropCard}
        />
      </div>
      <div className="player-hands-section">
        {gameState.playerHands.map((hand, index) => (
          <PlayerHand
            key={index}
            player={index}
            cards={hand}
            isCurrent={gameState.currentPlayer === index}
          />
        ))
        .filter(playerHand => playerHand.props.isCurrent)}
      </div>
      {gameState.gameOver && (
        <div className="game-over-section">
          <h2>Game Over</h2>
          <p>Winner: Player {gameState.winner + 1}</p>
          <button onClick={onRestart}>Play Again</button>
        </div>
      )}
    </div>
  );
}

export default GameBoard;
