import React from 'react';

const CapturedCards = ({ player, cards }) => {
  // Show only the topmost card face-up, others hidden beneath
  const topCard = cards.length > 0 ? cards[cards.length - 1] : null;

  return (
    <div className="captured-cards">
      <h4>{`Player ${player + 1} Captured Cards`}</h4>
      <div className="cards-container" style={{ position: 'relative', width: '60px', height: '90px' }}>
        {topCard ? (
          <div
            className="card"
            style={{
              position: 'absolute',
              top: 0,
              left: 0,
              width: '60px',
              height: '90px',
              border: '1px solid black',
              borderRadius: '5px',
              backgroundColor: 'white',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              fontSize: '24px',
              userSelect: 'none',
              cursor: 'pointer',
              zIndex: cards.length, // top card on top
            }}
            title={`Captured card: ${topCard.rank}${topCard.suit}`}
          >
            {topCard.rank}{topCard.suit}
          </div>
        ) : (
          <div>No captured cards</div>
        )}
      </div>
    </div>
  );
};

export default CapturedCards;
