import React, { useState, useCallback } from 'react';
import PlayerHand from './PlayerHand';
import TableCards from './TableCards';
import { useDrop } from 'react-dnd';

import {
  initializeGame,
  handleBuild,
  handleTrail,
  handleCapture,
} from './game-logic';
import CapturedCards from './CapturedCards';

const ItemTypes = {
  CARD: 'card',
};

function GameBoard({ onRestart }) {
  const [gameState, setGameState] = useState(initializeGame());

  const handleTrailCard = useCallback((card, player) => {
    setGameState(currentGameState => {
      if (player !== currentGameState.currentPlayer) {
        alert("It's not your turn!");
        return currentGameState;
      }
      // handleTrail returns the new state, which will be used to update the game state.
      return handleTrail(currentGameState, card);
    });
  }, []); // No dependency on gameState, so this function is stable.

  // New handler for dropping a card on another card
  const handleDropOnCard = useCallback((draggedItem, targetCard) => {
    setGameState(currentGameState => {
      const currentPlayer = currentGameState.currentPlayer;
      const playerHand = currentGameState.playerHands[currentPlayer];
      const playerCaptures = currentGameState.playerCaptures[currentPlayer];

      // Check if dragged card is in player's hand
      const hasDraggedCard = playerHand.some(
        c => c.rank === draggedItem.card.rank && c.suit === draggedItem.card.suit
      );
      if (!hasDraggedCard) {
        alert("You can only play cards from your hand.");
        return currentGameState;
      }

      // Implement simplified validation logic based on user description
      // Check if player has a card in hand that sums with dragged and target card to 9
      const targetRankValue = rankValue(targetCard.rank);
      const draggedRankValue = rankValue(draggedItem.card.rank);

      // Find if player has a card that sums with dragged and target to 9
      const neededValue = 9 - (targetRankValue + draggedRankValue);
      const hasNeededCard = playerHand.some(c => rankValue(c.rank) === neededValue);

      if (hasNeededCard) {
        const buildConfirmed = window.confirm(`You can build 9 by combining ${draggedItem.card.rank}${draggedItem.card.suit} and ${targetCard.rank}${targetCard.suit} with a card of value ${neededValue}. Build?`);
        if (buildConfirmed) {
          // Call handleBuild from game-logic (to be implemented)
          return handleBuild(currentGameState, draggedItem.card, targetCard, neededValue);
        }
      } else {
        alert("You do not have the required card to build 9.");
      }

      // For now, return current state unchanged
      return currentGameState;
    });
  }, [rankValue]);

  const [{ isOver, canDrop }, drop] = useDrop(() => ({
    accept: ItemTypes.CARD,
    drop: (item) => handleTrailCard(item.card, item.player),
    collect: (monitor) => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop(),
    }),
  }), [handleTrailCard]);

  const rankValue = (rank) => {
    if (rank === 'A') return 1;
    if (rank === 'J') return 11;
    if (rank === 'Q') return 12;
    if (rank === 'K') return 13;
    return parseInt(rank, 10);
  };

  const isActive = isOver && canDrop;

  return (
    <div ref={drop} className={`game-board ${isActive ? 'active-drop' : ''}`}>
      <div className="status-section">
        <p>Round: {gameState.round}</p>
      </div>
      <div className="captured-cards-section">
        {gameState.playerCaptures.map((capturedCards, index) => (
          <CapturedCards key={index} player={index} cards={capturedCards} />
        ))}
      </div>
      <div className="table-cards-section">
        <TableCards
          cards={gameState.tableCards}
          onDropOnCard={handleDropOnCard}
        />
      </div>
      <div className="player-hands-section">
        {gameState.playerHands
          .map((hand, index) => (
            <PlayerHand
              key={index}
              player={index}
              cards={hand}
              isCurrent={gameState.currentPlayer === index}
            />
          ))}
      </div>
      {gameState.gameOver && (
        <div className="game-over-section">
          <h2>Game Over</h2>
          <p>Winner: Player {gameState.winner + 1}</p>
          <button onClick={onRestart}>Play Again</button>
        </div>
      )}
    </div>
  );
}

export default GameBoard;
