import React from 'react';
import { useDrop } from 'react-dnd';
import Card from './Card';

const ItemTypes = {
  CARD: 'card',
};

const TableCards = ({ cards, onDropCard }) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: ItemTypes.CARD,
    drop: (item, monitor) => onDropCard(item.card, item.player), // Pass the dropped card and player info
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  }));

  return (
    <div ref={drop} className={`table-cards ${isOver ? 'hovered' : ''}`}>
      <h3>Table Cards</h3>
      <div className="cards-container" style={{ display: 'flex', flexWrap: 'wrap', gap: '10px', justifyContent: 'center', minHeight: '120px' }}>
        {cards.map((item, index) => {
          // Handle builds, which are groups of cards
          if (item.type === 'build') {
            return (
              <div key={index} className="build" style={{ border: '2px dashed green', padding: '5px', borderRadius: '10px' }}>
                {item.cards.map((card, cardIndex) => (
                  <Card key={cardIndex} rank={card.rank} suit={card.suit} />
                ))}
              </div>
            );
          }
          // Handle regular, single cards
          return (
            <Card key={index} rank={item.rank} suit={item.suit} />
          );
        })}
      </div>
    </div>
  );
};

export default TableCards;
