import React from 'react';
import { useDrag } from 'react-dnd';
import Card from './Card';

const ItemTypes = {
  CARD: 'card',
};

const PlayerHand = ({ player, cards, isCurrent, onSelectCard, selectedCard }) => {
  return (
    <div className={`player-hand ${isCurrent ? 'current-player' : ''}`}>
      <h3>{`Player ${player + 1} ${isCurrent ? '(Your Turn)' : ''}`}</h3>
      <div className="cards-container">
        {cards.map((card, index) => {
          const [{ isDragging }, drag] = useDrag(() => ({
            type: ItemTypes.CARD,
            item: { card, player },
            canDrag: isCurrent, // Only current player can drag their cards
            collect: (monitor) => ({
              isDragging: monitor.isDragging(),
            }),
          }));

          const isSelected = selectedCard && card.rank === selectedCard.rank && card.suit === selectedCard.suit;

          return (
            <div
              key={index}
              ref={drag}
              style={{ opacity: isDragging ? 0.5 : 1 }}
              onClick={() => {
                if (isCurrent && typeof onSelectCard === 'function') {
                  console.log('Card clicked:', card);
                  onSelectCard(card);
                }
              }}
            >
              {isCurrent ? (
                <Card
                  rank={card.rank}
                  suit={card.suit}
                  isSelected={isSelected}
                  onClick={() => {
                    if (isCurrent && typeof onSelectCard === 'function') {
                      onSelectCard(card);
                    }
                  }}
                />
              ) : (
                <Card rank="X" suit="spades" isSelected={false} onClick={() => {}} />
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default PlayerHand;
