import React, { useState, useCallback } from 'react';
import PlayerHand from './PlayerHand';
import TableCards from './TableCards';

import {
  initializeGame,
  handleDrop,
  handleBuild,
  handleCapture,
} from './game-logic';
import CapturedCards from './CapturedCards';

function GameBoard({ onRestart }) {
  const [gameState, setGameState] = useState(initializeGame());
  const [contextualMenu, setContextualMenu] = useState({
    isOpen: false,
    options: [],
    draggedCard: null,
    targetItem: null,
    position: { x: 0, y: 0 },
  });

  const resetActionState = useCallback(() => {
    setContextualMenu({ isOpen: false, options: [], draggedCard: null, targetItem: null, position: { x: 0, y: 0 } });
  }, []);

  // Handles dropping a card on an empty area of the table
  const handleCardDrop = useCallback((card, player) => {
    if (player !== gameState.currentPlayer) {
      alert("It's not your turn! You cannot drop cards now.");
      return;
    }
    const newGameState = handleDrop(gameState, card);
    setGameState(newGameState);
    resetActionState();
  }, [gameState, resetActionState]);

  const rankValue = (rank) => {
    if (rank === 'A') return 1;
    if (rank === 'J') return 11;
    if (rank === 'Q') return 12;
    if (rank === 'K') return 13;
    return parseInt(rank, 10);
  };

  // Handles dropping a card onto another card/build on the table
  const handleDropOnCard = useCallback((draggedCard, targetItem, event) => {
    if (draggedCard.player !== gameState.currentPlayer) {
      alert("It's not your turn! You cannot drop cards now.");
      return;
    }
    if (targetItem.type === 'build' || draggedCard.rank !== targetItem.rank) {
      handleCardDrop(draggedCard, gameState.currentPlayer);
      return;
    }

    const playerHand = gameState.playerHands[gameState.currentPlayer];
    const options = [];

    // Option 1: Capture the loose card
    options.push({ type: 'capture', label: `Capture` });

    // Option 2: Build a combined value (e.g., 4+4=8)
    const combinedValue = rankValue(draggedCard.rank) + rankValue(targetItem.rank);
    const canCaptureCombined = playerHand.some(c => rankValue(c.rank) === combinedValue && c !== draggedCard);
    if (canCaptureCombined) {
      options.push({ type: 'build', label: `Build ${combinedValue}`, value: combinedValue });
    }

    // Option 3: Create a new build with the same rank (e.g., build a 4)
    const canCaptureNewBase = playerHand.filter(c => c.rank === draggedCard.rank).length > 1;
    if (canCaptureNewBase) {
      options.push({ type: 'build', label: `Build ${draggedCard.rank}`, value: rankValue(draggedCard.rank) });
    }

    if (options.length > 0) {
      setContextualMenu({
        isOpen: true,
        options,
        draggedCard,
        targetItem,
        position: { x: event.clientX, y: event.clientY },
      });
    } else {
      handleCardDrop(draggedCard, gameState.currentPlayer);
    }
  }, [gameState, handleCardDrop]);

  const handleContextualAction = (action) => {
    const { draggedCard, targetItem } = contextualMenu;
    let newGameState;

    if (draggedCard.player !== gameState.currentPlayer) {
      alert("It's not your turn! Action cancelled.");
      resetActionState();
      return;
    }

    if (action.type === 'capture') {
      newGameState = handleCapture(gameState, draggedCard, [targetItem]);
    } else if (action.type === 'build') {
      newGameState = handleBuild(gameState, draggedCard, [targetItem], action.value);
    }

    if (newGameState && newGameState !== gameState) {
      setGameState(newGameState);
    }
    resetActionState();
  };

  return (
    <div className="game-board">
      <div className="status-section">
        <p>Round: {gameState.round}</p>
      </div>
      <div className="captured-cards-section" style={{ display: 'flex', flexDirection: 'row', justifyContent: 'center', gap: '20px' }}>
        {gameState.playerCaptures.map((capturedCards, index) => (
          <CapturedCards key={index} player={index} cards={capturedCards} />
        ))}
      </div>
      <div className="table-cards-section">
        <TableCards
          cards={gameState.tableCards}
          onDropCard={handleCardDrop}
          onDropOnCard={handleDropOnCard}
        />
      </div>
      <div className="player-hands-section" style={{ display: 'flex', flexDirection: 'row', justifyContent: 'center', gap: '10px', overflowX: 'auto' }}>
        {gameState.playerHands
          .map((hand, index) => (
            <PlayerHand
              key={index}
              player={index}
              cards={hand}
              isCurrent={gameState.currentPlayer === index}
              onSelectCard={() => {}} // Click selection is replaced by drag-drop
              selectedCard={null}
            />
          ))}
      </div>
      {contextualMenu.isOpen && (
        <div className="action-popup" style={{ position: 'fixed', top: contextualMenu.position.y, left: contextualMenu.position.x, backgroundColor: 'white', border: '1px solid black', padding: '10px', zIndex: 100, display: 'flex', flexDirection: 'column', gap: '5px' }}>
          <p style={{ margin: 0, fontWeight: 'bold' }}>Choose an action:</p>
          {contextualMenu.options.map((option) => (
            <button key={option.type + option.label} onClick={() => handleContextualAction(option)}>
              {option.label}
            </button>
          ))}
          <button onClick={resetActionState}>Cancel</button>
        </div>
      )}
      {gameState.gameOver && (
        <div className="game-over-section">
          <h2>Game Over</h2>
          <p>Winner: Player {gameState.winner + 1}</p>
          <button onClick={onRestart}>Play Again</button>
        </div>
      )}
    </div>
  );
}

export default GameBoard;
