import React from 'react';
import { useDrag } from 'react-dnd';
import Card from './Card';

const ItemTypes = {
  CARD: 'card',
};

const DraggableCard = ({ card, player, isCurrent, isSelected, onSelectCard }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.CARD,
    item: { card, player },
    canDrag: isCurrent,
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
    // Optional: Add begin and end drag handlers for feedback
    begin: () => {
      if (!isCurrent) {
        alert("You can only drag your own cards during your turn.");
        return false;
      }
      return { card, player };
    },
  }), [card, player, isCurrent]);

  return (
    <div
      ref={drag}
      style={{ opacity: isDragging ? 0.5 : 1 }}
    >
      <Card
        rank={card.rank}
        suit={card.suit}
        isSelected={isSelected}
        onClick={() => {
          if (isCurrent && typeof onSelectCard === 'function') {
            onSelectCard(card);
          }
        }}
      />
    </div>
  );
};

export default DraggableCard;
