import React from 'react';
import './Card.css';

const suitSymbols = {
  hearts: '♥',
  diamonds: '♦',
  clubs: '♣',
  spades: '♠',
};

const suitColors = {
  hearts: 'red',
  diamonds: 'red',
  clubs: 'black',
  spades: 'black',
};

function Card({ rank, suit, isSelected, onClick }) {
  const suitSymbol = suitSymbols[suit] || '';
  const color = suitColors[suit] || 'black';

  return (
    <div
      className={`card${isSelected ? ' selected' : ''}`}
      onClick={onClick}
      role="button"
      tabIndex={0}
      onKeyPress={(e) => { if (e.key === 'Enter') onClick(); }}
      aria-label={`${rank} of ${suit}`}
    >
      <div className="card-corner top-left" style={{ color }}>
        <div className="rank">{rank}</div>
        <div className="suit">{suitSymbol}</div>
      </div>
      <div className="card-center" style={{ color }}>
        {suitSymbol}
      </div>
      <div className="card-corner bottom-right" style={{ color }}>
        <div className="rank">{rank}</div>
        <div className="suit">{suitSymbol}</div>
      </div>
    </div>
  );
}

export default Card;
