import React from 'react';

const CapturedCards = ({ player, cards }) => {
  // Show only the topmost card face-up, others hidden beneath
  const topCard = cards.length > 0 ? cards[cards.length - 1] : null;

  return (
    <div className="captured-cards">
      <h4>{`Player ${player + 1} Captured Cards`}</h4>
      <div className="cards-container" style={{ display: 'flex', flexDirection: 'row', flexWrap: 'wrap', gap: '5px' }}>
        {cards.length > 0 ? (
          cards.map((card, index) => (
            <div
              key={index}
              className="card"
              style={{
                width: '40px',
                height: '60px',
                border: '1px solid black',
                borderRadius: '5px',
                backgroundColor: 'white',
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                fontSize: '18px',
                userSelect: 'none',
                cursor: 'default',
              }}
              title={`Captured card: ${card.rank}${card.suit}`}
            >
              {card.rank}{card.suit}
            </div>
          ))
        ) : (
          <div>No captured cards</div>
        )}
      </div>
    </div>
  );
};

export default CapturedCards;
