import React from 'react';
import CardStack from './CardStack';

const TableCards = ({ cards }) => {
  // Group loose cards by their rank.
  const looseCardGroups = cards
    .filter(c => !c.type)
    .reduce((groups, card) => {
      (groups[card.rank] = groups[card.rank] || []).push(card);
      return groups;
    }, {});

  const builds = cards.filter(c => c.type === 'build');

  // Handler for dropping a card on another card
  const handleDropOnCard = (draggedItem, targetCard) => {
    // For now, prompt the player to choose build or capture
    const choice = window.prompt(`You dropped ${draggedItem.card.rank}${draggedItem.card.suit} on ${targetCard.rank}${targetCard.suit}. Type "build" or "capture":`);
    if (choice === 'build') {
      console.log('Player chose to build');
      // Implement build logic here later
    } else if (choice === 'capture') {
      console.log('Player chose to capture');
      // Implement capture logic here later
    } else {
      console.log('Invalid choice or cancelled');
    }
  };

  return (
    <div className="table-cards">
      <h3>Table Cards</h3>
      <div className="cards-container">
        {/* Render each group of loose cards as a stack */}
        {Object.values(looseCardGroups).map((group, index) =>
          <CardStack
            key={`loose-stack-${index}`}
            stackId={`loose-stack-${index}`}
            cards={group}
            onDropOnCard={handleDropOnCard}
          />
        )}
        {/* Render each build as a stack inside its own container */}
        {builds.map((build, index) => (
          <div key={`build-container-${index}`} className="build">
            <CardStack
              stackId={`build-stack-${index}`}
              cards={build.cards}
              onDropOnCard={handleDropOnCard}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default TableCards;
