import React, { useState } from 'react';
import PlayerHand from './PlayerHand';
import TableCards from './TableCards';

import {
  initializeGame,
  handleDrop,
  handleBuild,
  handleCapture,
  isRoundOver,
  isGameOver,
  calculateScores,
  findValidCaptures,
  findValidBuilds,
} from './game-logic';
import CapturedCards from './CapturedCards';

function GameBoard({ onRestart }) {
  const [gameState, setGameState] = useState(initializeGame());
  const [selectedCard, setSelectedCard] = React.useState(null);
  const [possibleCaptures, setPossibleCaptures] = React.useState([]);
  const [possibleBuilds, setPossibleBuilds] = React.useState([]);
  const [actionChoice, setActionChoice] = React.useState(null);

  const resetActionState = () => {
    setSelectedCard(null);
    setPossibleCaptures([]);
    setPossibleBuilds([]);
    setActionChoice(null);
  };

  const handleCardPlay = (card, player) => {
    if (player !== gameState.currentPlayer) {
      console.log("It's not your turn!");
      return;
    }

    setSelectedCard(card);
    const captures = findValidCaptures(card, gameState.tableCards);
    const builds = findValidBuilds(card, gameState.tableCards);
    setPossibleCaptures(captures);
    setPossibleBuilds(builds);

    if (captures.length === 0 && builds.length === 0) {
      // No capture or build possible, default to drop
      const newGameState = handleDrop(gameState, card);
      setGameState(newGameState);
      resetActionState();
    }
  };

  const handleSelectCard = (card) => {
    if (gameState.currentPlayer !== null) {
      setSelectedCard(card);
      const captures = findValidCaptures(card, gameState.tableCards);
      const builds = findValidBuilds(card, gameState.tableCards);
      setPossibleCaptures(captures);
      setPossibleBuilds(builds);
    }
  };

  const handleAction = (action) => {
    if (!selectedCard) return;

    let newGameState = { ...gameState };

    if (action === 'capture' && possibleCaptures.length > 0) {
      // For simplicity, take the first valid capture
      newGameState = handleCapture(gameState, selectedCard, possibleCaptures[0]);
    } else if (action === 'build' && possibleBuilds.length > 0) {
      // For simplicity, take the first valid build
      newGameState = handleBuild(gameState, selectedCard, possibleBuilds[0]);
    } else if (action === 'drop') {
      newGameState = handleDrop(gameState, selectedCard);
    }

    setGameState(newGameState);
    resetActionState();
  };

  return (
    <div className="game-board">
      <div className="status-section">
        <p>Round: {gameState.round}</p>
        <p>Current Player: Player {gameState.currentPlayer + 1}</p>
        <p>Scores: Player 1 ({gameState.scores[0]}) - Player 2 ({gameState.scores[1]})</p>
      </div>
      <div className="captured-cards-section">
        {gameState.playerCaptures.map((capturedCards, index) => (
          <CapturedCards key={index} player={index} cards={capturedCards} />
        ))}
      </div>
      <div className="table-cards-section">
        <TableCards
          cards={gameState.tableCards}
          onDropCard={handleCardPlay}
        />
      </div>
      <div className="player-hands-section">
        {gameState.playerHands
          .map((hand, index) => ({ hand, index }))
          .filter(({ index }) => gameState.currentPlayer === index)
          .map(({ hand, index }) => (
            <PlayerHand
              key={index}
              player={index}
              cards={hand}
              isCurrent={true}
              onSelectCard={handleSelectCard}
              selectedCard={selectedCard}
            />
          ))}
      </div>
      {selectedCard && (
        <div className="action-buttons">
          {possibleCaptures.length > 0 && (
            <button onClick={() => handleAction('capture')}>Capture</button>
          )}
          {possibleBuilds.length > 0 && (
            <button onClick={() => handleAction('build')}>Build</button>
          )}
          <button onClick={() => handleAction('drop')}>Drop</button>
        </div>
      )}
      {gameState.gameOver && (
        <div className="game-over-section">
          <h2>Game Over</h2>
          <p>Winner: Player {gameState.winner + 1}</p>
          <button onClick={onRestart}>Play Again</button>
        </div>
      )}
    </div>
  );
}

export default GameBoard;
