import React, { useState, useCallback } from 'react';
import PlayerHand from './PlayerHand';
import TableCards from './TableCards';
import { useDrop } from 'react-dnd';
import {
  initializeGame,
  handleBuild,
  rankValue,
  handleTrail,
  handleCapture,
} from './game-logic';
import CapturedCards from './CapturedCards';
import ActionModal from './ActionModal';

const ItemTypes = {
  CARD: 'card',
};

function GameBoard({ onRestart }) {
  const [gameState, setGameState] = useState(initializeGame());
  const [modalInfo, setModalInfo] = useState(null);

  const handleTrailCard = useCallback((card, player) => {
    setGameState(currentGameState => {
      // handleTrail returns the new state, which will be used to update the game state.
      return handleTrail(currentGameState, card);
    });
  }, []);

  const handleModalAction = useCallback((action) => {
    setGameState(currentGameState => {
      const { draggedCard, targetCard } = action.payload;
      switch (action.type) {
        case 'capture':
          return handleCapture(currentGameState, draggedCard, [targetCard]);
        case 'build':
          return handleBuild(currentGameState, draggedCard, [targetCard], action.buildValue);
        default:
          return currentGameState;
      }
    });
    setModalInfo(null); // Close the modal after action
  }, []);

  // New handler for dropping a card on another card
  const handleDropOnCard = useCallback((draggedItem, targetInfo) => {
    setGameState(currentGameState => {
      const { currentPlayer, playerHands, tableCards } = currentGameState;
      const playerHand = playerHands[currentPlayer];
      const draggedCard = draggedItem.card;

      // 1. Check if it's the current player's turn.
      if (draggedItem.player !== currentPlayer) {
        alert("It's not your turn!");
        return currentGameState;
      }

      // CASE 1: Dropped on a BUILD (Capture only)
      if (targetInfo.type === 'build') {
        const build = tableCards.find(c => c.buildId === targetInfo.buildId);
        if (!build) return currentGameState;

        if (rankValue(draggedCard.rank) === build.value) {
          return handleCapture(currentGameState, draggedCard, build.cards);
        }
        alert(`Cannot use a ${draggedCard.rank} to capture a build of value ${build.value}.`);
        return currentGameState;
      }

      // CASE 2: Dropped on a LOOSE CARD
      if (targetInfo.type === 'loose') {
        const looseCard = tableCards.find(c => !c.type && c.rank === targetInfo.rank && c.suit === targetInfo.suit);
        if (!looseCard) return currentGameState; // Card might have been captured

        // --- AMBIGUITY ANALYSIS ---
        // Case A: Dropped card has the same rank as the table card (e.g., 6 on 6)
        if (rankValue(draggedCard.rank) === rankValue(looseCard.rank)) {
          const remainingHand = playerHand.filter(c => c.rank !== draggedCard.rank || c.suit !== draggedCard.suit);
          const possibleActions = [];

          // Action 1: Capture (always possible in this case)
          possibleActions.push({
            type: 'capture',
            label: `Capture ${looseCard.rank}`,
            payload: { draggedCard, targetCard: looseCard }
          });

          // Action 2: Set Build (e.g., 6 on 6 to build a 6)
          const setBuildValue = rankValue(draggedCard.rank);
          if (remainingHand.some(c => rankValue(c.rank) === setBuildValue)) {
            possibleActions.push({
              type: 'build',
              label: `Build ${setBuildValue}`,
              buildValue: setBuildValue,
              payload: { draggedCard, targetCard: looseCard }
            });
          }

          // Action 3: Sum Build (e.g., 4 on 4 to build an 8)
          const sumBuildValue = rankValue(draggedCard.rank) + rankValue(looseCard.rank);
          if (sumBuildValue <= 10 && remainingHand.some(c => rankValue(c.rank) === sumBuildValue)) {
            possibleActions.push({
              type: 'build',
              label: `Build ${sumBuildValue}`,
              buildValue: sumBuildValue,
              payload: { draggedCard, targetCard: looseCard }
            });
          }

          if (possibleActions.length > 1) {
            setModalInfo({
              title: 'Choose Your Action',
              message: `You played a ${draggedCard.rank} on a ${looseCard.rank}. What would you like to do?`,
              actions: possibleActions
            });
            return currentGameState; // Wait for modal choice
          } else {
            return handleCapture(currentGameState, draggedCard, [looseCard]); // Only one option, auto-execute
          }
        }

        // Case B: Dropped card has a different rank (standard build attempt)
        const buildValue = rankValue(draggedCard.rank) + rankValue(looseCard.rank);
        const canCreateBuild = playerHand.some(c => rankValue(c.rank) === buildValue && (c.rank !== draggedCard.rank || c.suit !== draggedCard.suit));

        if (canCreateBuild) {
          return handleBuild(currentGameState, draggedCard, [looseCard], buildValue);
        }

        alert("Invalid move. You cannot build or capture with these cards.");
        return currentGameState;
      }

      // Default case if targetInfo.type is unknown
      return currentGameState;
    }); // No dependencies needed for setGameState updater form
  }, []); // The function itself is stable

  const [{ isOver, canDrop }, drop] = useDrop(() => ({
    accept: ItemTypes.CARD,
    drop: (item, monitor) => {
      if (monitor.didDrop()) {
        return;
      }
      handleTrailCard(item.card, item.player);
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop(),
    }),
  }), [handleTrailCard]);

  const isActive = isOver && canDrop;

  return (
    <div ref={drop} className={`game-board ${isActive ? 'active-drop' : ''}`}>
      <div className="status-section">
        <p>Round: {gameState.round}</p>
      </div>
      <div className="captured-cards-positioned">
        {gameState.playerCaptures.map((capturedCards, index) => (
          <CapturedCards key={index} player={index} cards={capturedCards} />
        ))}
      </div>
      <div className="table-cards-section">
        <TableCards
          cards={gameState.tableCards}
          onDropOnCard={handleDropOnCard}
        />
      </div>
      <div className="player-hands-section">
        {gameState.playerHands
          .map((hand, index) => (
            <PlayerHand
              key={index}
              player={index}
              cards={hand}
              isCurrent={gameState.currentPlayer === index}
            />
          ))}
      </div>
      <ActionModal
        modalInfo={modalInfo}
        onAction={handleModalAction}
        onCancel={() => setModalInfo(null)}
      />
      {gameState.gameOver && (
        <div className="game-over-section">
          <h2>Game Over</h2>
          <p>Winner: Player {gameState.winner + 1}</p>
          <button onClick={onRestart}>Play Again</button>
        </div>
      )}
    </div>
  );
}

export default GameBoard;
