import React, { useState, useEffect } from 'react';
import PlayerHand from './PlayerHand';
import TableCards from './TableCards';
import DeckDisplay from './DeckDisplay';
import {
  initializeGame,
  handleDrop,
  handleBuild,
  handleCapture,
  isRoundOver,
  isGameOver,
  calculateScores,
} from './game-logic';

function GameBoard({ onRestart }) {
  const [gameState, setGameState] = useState(initializeGame());
  const [selectedCard, setSelectedCard] = useState(null);
  const [selectedTableCards, setSelectedTableCards] = useState([]);

  const handleAction = (action) => {
    let newGameState;

    switch (action) {
      case 'drop':
        newGameState = handleDrop(gameState, selectedCard);
        break;
      case 'build':
        newGameState = handleBuild(gameState, selectedCard, selectedTableCards);
        break;
      case 'capture':
        newGameState = handleCapture(gameState, selectedCard, selectedTableCards);
        break;
      default:
        return;
    }

    if (isGameOver(newGameState)) {
      const scores = calculateScores(newGameState.playerCaptures);
      const winner = scores[0] > scores[1] ? 0 : 1;
      setGameState({ ...newGameState, gameOver: true, scores, winner });
    } else if (isRoundOver(newGameState)) {
      // Start a new round
      const scores = calculateScores(newGameState.playerCaptures);
      const newRoundState = initializeGame();
      newRoundState.round = 2;
      newRoundState.scores = scores;
      setGameState(newRoundState);
    } else {
      setGameState(newGameState);
    }

    setSelectedCard(null);
    setSelectedTableCards([]);
  };

  return (
    <div className="game-board">
      <div className="status-section">
        <p>Round: {gameState.round}</p>
        <p>Current Player: Player {gameState.currentPlayer + 1}</p>
        <p>Scores: Player 1 ({gameState.scores[0]}) - Player 2 ({gameState.scores[1]})</p>
      </div>
      <div className="table-cards-section">
        <TableCards
          cards={gameState.tableCards}
          selectedCards={selectedTableCards}
          onSelectCard={setSelectedTableCards}
        />
      </div>
      <div className="player-hands-section">
        <PlayerHand
          player={0}
          cards={gameState.playerHands[0]}
          isCurrent={gameState.currentPlayer === 0}
          selectedCard={selectedCard}
          onSelectCard={setSelectedCard}
        />
        <PlayerHand
          player={1}
          cards={gameState.playerHands[1]}
          isCurrent={gameState.currentPlayer === 1}
          selectedCard={selectedCard}
          onSelectCard={setSelectedCard}
        />
      </div>
      <div className="actions-section">
        <button onClick={() => handleAction('drop')} disabled={!selectedCard}>
          Drop Card
        </button>
        <button onClick={() => handleAction('build')} disabled={!selectedCard || selectedTableCards.length === 0}>
          Build
        </button>
        <button onClick={() => handleAction('capture')} disabled={!selectedCard || selectedTableCards.length === 0}>
          Capture
        </button>
      </div>
      <div className="deck-display-section">
        <DeckDisplay deck={gameState.deck} />
      </div>
      {gameState.gameOver && (
        <div className="game-over-section">
          <h2>Game Over</h2>
          <p>Winner: Player {gameState.winner + 1}</p>
          <button onClick={onRestart}>Play Again</button>
        </div>
      )}
    </div>
  );
}

export default GameBoard;
