import React from 'react';
import { useDrop } from 'react-dnd';
import Card from './Card';

const ItemTypes = {
  CARD: 'card',
};

const TableCards = ({ cards, onDropCard }) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: ItemTypes.CARD,
    drop: (item, monitor) => onDropCard(item.card, item.player), // Pass the dropped card and player info
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  }));

  // Helper to sort cards descending by rank value
  const rankValue = (rank) => {
    if (rank === 'A') return 14;
    if (rank === 'K') return 13;
    if (rank === 'Q') return 12;
    if (rank === 'J') return 11;
    return parseInt(rank, 10);
  };

  // Group loose cards by stack (for simplicity, group all loose cards as separate stacks)
  // In a real game, you might have stack IDs or grouping info
  const looseCards = cards.filter(c => c.type !== 'build');
  const builds = cards.filter(c => c.type === 'build');

  // Sort loose cards descending by rank
  const sortedLooseCards = [...looseCards].sort((a, b) => rankValue(b.rank) - rankValue(a.rank));

  // Render stacked cards with absolute positioning and z-index
  const renderStack = (stackCards, keyPrefix) => {
    return (
      <div key={keyPrefix} style={{ position: 'relative', width: '65px', height: `${90 + (stackCards.length - 1) * 15}px`, margin: '0 10px' }}>
        {stackCards.map((card, idx) => (
          <div key={idx} style={{
            position: 'absolute',
            top: `${idx * 15}px`,
            left: 0,
            zIndex: idx,
          }}>
            <Card rank={card.rank} suit={card.suit} />
          </div>
        ))}
      </div>
    );
  };

  return (
    <div ref={drop} className={`table-cards ${isOver ? 'hovered' : ''}`}>
      <h3>Table Cards</h3>
      <div className="cards-container" style={{ display: 'flex', flexWrap: 'nowrap', justifyContent: 'center', minHeight: '120px' }}>
        {sortedLooseCards.length > 0 && renderStack(sortedLooseCards, 'loose')}
        {builds.map((build, index) => (
          <div key={index} className="build" style={{ border: '2px dashed green', padding: '5px', borderRadius: '10px', marginLeft: '10px' }}>
            {build.cards.map((card, cardIndex) => (
              <Card key={cardIndex} rank={card.rank} suit={card.suit} />
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default TableCards;
