import React from 'react';
import { useDrop } from 'react-dnd';

const ItemTypes = {
  CARD: 'card',
};

const TableCards = ({ cards, onDropCard }) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: ItemTypes.CARD,
    drop: (item, monitor) => onDropCard(item.card, item.player), // Pass the dropped card and player info
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  }));

  return (
    <div ref={drop} className={`table-cards ${isOver ? 'hovered' : ''}`}>
      <h3>Table Cards</h3>
      <div className="cards-container">
        {cards.map((card, index) => (
          <div
            key={index}
            className="card"
          >
            {card.rank}{card.suit}
          </div>
        ))}
      </div>
    </div>
  );
};

export default TableCards;
