 * @param {object} selectedCard - The card selected from the player's hand.
 * @param {Array} selectedTableCards - The cards selected from the table.
 * @returns {object} The new game state.
 */
export const handleBuild = (gameState, selectedCard, selectedTableCards) => {
  const {
    playerHands,
    tableCards,
    currentPlayer
  } = gameState;
  const allCardsInBuild = [selectedCard, ...selectedTableCards];
  const buildValue = allCardsInBuild.reduce((sum, card) => sum + card.value, 0);

