  const newBuild = {
    type: 'build',
    cards: allCardsInBuild,
    value: buildValue,
    owner: currentPlayer
  };

  // Remove the selected card from the player's hand
  const newPlayerHands = [...playerHands];
  newPlayerHands[currentPlayer] = newPlayerHands[currentPlayer].filter(c => c !== selectedCard);

  // Remove the selected cards from the table
  const newTableCards = tableCards.filter(c => !selectedTableCards.includes(c));
  newTableCards.push(newBuild);
  return {
    ...gameState,
    playerHands: newPlayerHands,
    tableCards: newTableCards,
    currentPlayer: (currentPlayer + 1) % 2
  };
