import React from 'react';

const suitSymbols = {
  hearts: '♥',
  diamonds: '♦',
  clubs: '♣',
  spades: '♠',
};

const suitColors = {
  hearts: '#d40000',  /* bright red */
  diamonds: '#d40000', /* bright red */
  clubs: '#000000',    /* black */
  spades: '#000000',   /* black */
};

function Card({ rank, suit, isSelected, onClick }) {
  const suitSymbol = suitSymbols[suit] || '';
  const color = suitColors[suit] || 'black';

  return (
    <div
      className={`card${isSelected ? ' selected' : ''}`}
      onClick={onClick}
      role="button"
      tabIndex={0}
      onKeyPress={(e) => { if (e.key === 'Enter') onClick(); }}
      aria-label={`${rank} of ${suit}`}
      style={{
        border: isSelected ? '2px solid blue' : '1px solid black',
        borderRadius: '8px',
        width: '60px',
        height: '90px',
        backgroundColor: 'white',
        boxShadow: isSelected ? '0 0 10px blue' : 'none',
        cursor: 'pointer',
        userSelect: 'none',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        padding: '5px',
        fontFamily: 'Arial, sans-serif',
        position: 'relative',
      }}
    >
      <div className="card-corner top-left" style={{ color, fontSize: '16px', position: 'absolute', top: '5px', left: '5px', textAlign: 'left' }}>
        <div className="rank">{rank}</div>
        <div className="suit">{suitSymbol}</div>
      </div>
      <div className="card-corner bottom-right" style={{ color, fontSize: '16px', position: 'absolute', bottom: '5px', right: '5px', textAlign: 'right', transform: 'rotate(180deg)' }}>
        <div className="rank">{rank}</div>
        <div className="suit">{suitSymbol}</div>
      </div>
    </div>
  );
}

export default Card;
