import React from 'react';

const PlayerHand = ({ player, cards, isCurrent, selectedCard, onSelectCard }) => {
  return (
    <div className={`player-hand ${isCurrent ? 'current-player' : ''}`}>
      <h3>{`Player ${player + 1} ${isCurrent ? '(Your Turn)' : ''}`}</h3>
      <div className="cards-container">
        {cards.map((card, index) => (
          <div
            key={index}
            className={`card ${selectedCard === card ? 'selected' : ''}`}
            onClick={() => isCurrent && onSelectCard(card)}
          >
            {card.rank}{card.suit}
          </div>
        ))}
      </div>
    </div>
  );
};

export default PlayerHand;
