import React from 'react';
import { useDrop } from 'react-dnd';
import Card from './Card';

const ItemTypes = {
  CARD: 'card',
};

const TableCards = ({ cards, onDropCard, onDropOnCard }) => {
  const [{ isOver }, drop] = useDrop(() => ({
    accept: ItemTypes.CARD,
    drop: (item) => onDropCard(item.card, item.player),
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  }));

  const rankValue = (rank) => {
    if (rank === 'A') return 14;
    if (rank === 'K') return 13;
    if (rank === 'Q') return 12;
    if (rank === 'J') return 11;
    return parseInt(rank, 10);
  };

  // This function will render any array of cards as a visual stack.
  const renderStack = (stackCards, keyPrefix, onDropStack) => {
    const [{ isOverStack }, dropStack] = useDrop(() => ({
      accept: ItemTypes.CARD,
      drop: (item, monitor) => onDropStack(item.card, monitor.getClientOffset()),
      collect: (monitor) => ({
        isOverStack: monitor.isOver(),
      }),
    }));

    // Sort cards descending by rank to ensure largest is at the bottom.
    const sortedCards = [...stackCards].sort((a, b) => rankValue(b.rank) - rankValue(a.rank));

    return (
      <div ref={dropStack} key={keyPrefix} style={{ position: 'relative', width: '65px', height: `${95 + (sortedCards.length - 1) * 20}px`, margin: '0 5px', backgroundColor: isOverStack ? 'rgba(173, 216, 230, 0.5)' : 'transparent', borderRadius: '10px', padding: '5px' }}>
        {sortedCards.map((card, idx) => (
          <div key={`${keyPrefix}-${card.rank}-${card.suit}-${idx}`} style={{
            position: 'absolute',
            top: `${idx * 20}px`,
            left: 0,
            zIndex: idx,
          }}>
            <Card rank={card.rank} suit={card.suit} />
          </div>
        ))}
      </div>
    );
  };

  // Group loose cards by their rank.
  const looseCardGroups = cards
    .filter(c => !c.type)
    .reduce((groups, card) => {
      (groups[card.rank] = groups[card.rank] || []).push(card);
      return groups;
    }, {});

  const builds = cards.filter(c => c.type === 'build');

  return (
    <div ref={drop} className={`table-cards ${isOver ? 'hovered' : ''}`}>
      <h3>Table Cards</h3>
      <div className="cards-container" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', minHeight: '120px', alignItems: 'flex-start', gap: '10px' }}>
        {/* Render each group of loose cards as a stack */}
        {Object.values(looseCardGroups).map((group, index) =>
          renderStack(group, `loose-stack-${index}`, (draggedCard, position) => onDropOnCard(draggedCard, group[0], { clientX: position.x, clientY: position.y }))
        )}
        {/* Render each build as a stack inside its own container */}
        {builds.map((build, index) => (
          <div key={`build-container-${index}`} className="build" style={{ border: '2px dashed green', padding: '5px', borderRadius: '10px' }}>
            {renderStack(build.cards, `build-stack-${index}`, (draggedCard, position) => onDropOnCard(draggedCard, build, { clientX: position.x, clientY: position.y }))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default TableCards;
