import React from 'react';

const CapturedCards = ({ player, cards }) => {
  // Show only the topmost card face-up, others hidden beneath
  const topCard = cards.length > 0 ? cards[cards.length - 1] : null;

  return (
    <div className="captured-cards">
      <h4>{`Player ${player + 1} Captured Cards`}</h4>
      <div className="cards-container" style={{ position: 'relative', width: '60px', height: '90px' }}>
        {cards.length > 0 ? (
          cards.map((card, index) => {
            const isTopCard = index === cards.length - 1;
            return (
              <div
                key={index}
                className="card"
                style={{
                  position: 'absolute',
                  top: `${index * 1}px`,
                  left: 0,
                  width: '60px',
                  height: '90px',
                  border: '1px solid black',
                  borderRadius: '5px',
                  backgroundColor: 'white',
                  display: 'flex',
                  justifyContent: 'center',
                  alignItems: 'center',
                  fontSize: '24px',
                  userSelect: 'none',
                  cursor: isTopCard ? 'pointer' : 'default',
                  zIndex: index,
                  opacity: isTopCard ? 1 : 0.5,
                }}
                title={`Captured card: ${card.rank}${card.suit}`}
              >
                {card.rank}{card.suit}
              </div>
            );
          })
        ) : (
          <div>No captured cards</div>
        )}
      </div>
    </div>
  );
};

export default CapturedCards;
