import React, { useState } from 'react';
import GameBoard from './components/GameBoard';

import './App.css';

function App() {
  const [key, setKey] = useState(0);

  const handleRestart = () => {
    setKey((prev) => prev + 1);
  };

  return (
    <div className="App">
      <GameBoard key={key} onRestart={handleRestart} />
    </div>
  );
}

export default App;
