import React from 'react';
import CardStack from './CardStack';
import { useDrop } from 'react-dnd';
import { canDropCardOnTable } from './game-logic';

const TableCards = ({ cards, gameState, onCardDrop }) => {
  // Group loose cards by their rank.
  const looseCardGroups = cards
    .filter(c => !c.type)
    .reduce((groups, card) => {
      (groups[card.rank] = groups[card.rank] || []).push(card);
      return groups;
    }, {});

  const builds = cards.filter(c => c.type === 'build');

  const [{ isOver, canDrop }, drop] = useDrop({
    accept: 'card',
    canDrop: (item) => {
      return canDropCardOnTable(gameState, item.card);
    },
    drop: (item, monitor) => {
      if (monitor.canDrop()) {
        onCardDrop(item.card);
      }
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop(),
    }),
  });

  const isActive = isOver && canDrop;

  return (
    <div className="table-cards" ref={drop}>
      <h3>Table Cards</h3>
      <div className={`cards-container ${isActive ? 'active-drop' : ''} ${canDrop ? 'can-drop' : 'cannot-drop'}`} style={{ minHeight: '100px', border: '2px dashed gray', padding: '10px' }}>
        {/* Render each group of loose cards as a stack */}
        {Object.values(looseCardGroups).map((group, index) =>
          <CardStack
            key={`loose-stack-${index}`}
            stackId={`loose-stack-${index}`}
            cards={group}
            onDropStack={() => {}}
          />
        )}
        {/* Render each build as a stack inside its own container */}
        {builds.map((build, index) => (
          <div key={`build-container-${index}`} className="build">
            <CardStack
              stackId={`build-stack-${index}`}
              cards={build.cards}
              onDropStack={(card) => {
                // Handle drop on build stack
                console.log('Dropped card on build stack:', card);
                // TODO: Implement build stack drop logic
              }}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default TableCards;
