import React from 'react';

function PlayerHand({ player, cards, isCurrent, selectedCard, onSelectCard }) {
  return (
    <div className={`player-hand player-${player} ${isCurrent ? 'current-player' : ''}`}>
      <h3>Player {player + 1} {isCurrent ? '(Your Turn)' : ''}</h3>
      <div className="cards-container">
        {cards.length === 0 && <p>No cards</p>}
        {cards.map((card, index) => {
          const isSelected = selectedCard && selectedCard.rank === card.rank && selectedCard.suit === card.suit;
          return (
            <div
              key={index}
              className={`card ${isSelected ? 'selected' : ''}`}
              onClick={() => isCurrent && onSelectCard(card)}
              style={{ cursor: isCurrent ? 'pointer' : 'default' }}
            >
              {card.rank} {card.suit}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default PlayerHand;
